INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`, `TieuDe_en`, `content_en`, `TomTat_en`) VALUES
(1, 'Nạn cướp tiền bằng kim tiêm dính máu', 'Duy bị hai cô gái ăn vạ giữa đường cho rằng đã đụng vào xe của họ. 5 thanh niên khác lập tức xuất hiện buộc anh bồi thường, nếu không sẽ đâm kim tiêm có máu HIV.', 'http://vnexpress.net/Files/Subject/3B/A2/2E/0A/cuop_kim_tiem_2.jpg', '2010-11-13 00:00:00', '<p class="Normal">\r\n	&quot;L&uacute;c ấy em sợ qu&aacute;, định quay đầu xe th&aacute;o chạy th&igrave; ở sau lưng c&oacute; kẻ đ&atilde; g&iacute; dao v&agrave; kim ti&ecirc;m s&aacute;t người m&igrave;nh. Một t&ecirc;n m&oacute;c t&uacute;i em lấy to&agrave;n bộ số tiền 350.000 đồng v&agrave; một chiếc điện thoại nhưng vẫn ch&ecirc; &iacute;t, hắn bắt em giao lu&ocirc;n chiếc xe tay ga&quot;, Đỗ Ho&agrave;ng Duy, sinh vi&ecirc;n Đại học Mở TP HCM chưa hết b&agrave;ng ho&agrave;ng kể với <em>VnExpress.net</em>.</p>\r\n<p class="Normal">\r\n	Sau một hồi thương lượng, bọn trấn lột đồng &yacute; cho Duy đến một buồng ATM gần đ&oacute; r&uacute;t tiền để &quot;bồi thường thiệt hại&quot;, song một t&ecirc;n vẫn kh&ocirc;ng qu&ecirc;n ngồi sau k&egrave; k&egrave; kim ti&ecirc;m b&ecirc;n mạn sườn để &quot;&aacute;p tải&quot; cậu sinh vi&ecirc;n. Ch&uacute;ng đ&ograve;i cậu bấm m&aacute;y cho xem t&agrave;i khoản dư v&agrave; bắt r&uacute;t hết số tiền 2.300.000 đồng c&ograve;n lại.</p>\r\n<p class="Normal">\r\n	&quot;Bọn n&oacute; t&aacute;o tợn lắm, ch&uacute;ng n&oacute;i thẳng l&agrave; &#39;đang cần tiền để mua thuốc ch&iacute;ch&#39; v&agrave; bảo nếu em la l&ecirc;n ch&uacute;ng sẽ đ&acirc;m kim cho l&acirc;y HIV. Lần đầu ti&ecirc;n bị trấn lột kiểu n&agrave;y, em sợ qu&aacute; trao hết tiền cho ch&uacute;ng chỉ mong sao giữ lại chiếc xe m&aacute;y. Nghĩ lại cũng dại, gi&aacute; như l&uacute;c đầu m&igrave;nh chống xe xuống rồi kh&oacute;a cổ v&agrave; chạy đi k&ecirc;u cứu th&igrave; kh&ocirc;ng đến nỗi mất tiền&quot;, Duy kể vẻ nuối tiếc sau vụ việc.</p>\r\n<p class="Normal">\r\n	Tinh thần hoảng loạn n&ecirc;n sau khi được bọn ch&uacute;ng &quot;tha&quot;, Duy hớt hải chay về m&agrave; kh&ocirc;ng đến tr&igrave;nh diện cơ quan ch&iacute;nh quyền v&igrave; nghĩ rằng số tiền bị cướp kh&ocirc;ng lớn. Vụ việc xảy ra l&uacute;c 20h tr&ecirc;n đường Trần Hưng Đạo, quận 1, TP HCM.</p>\r\n<p class="Normal">\r\n	<img alt="khung khí dùng cướp" src="http://vnexpress.net/Files/Subject/3B/A2/2E/0A/cuop_kim_tiem_4.jpg" style="width: 490px; height: 350px;" /></p>\r\n<p class="Normal">\r\n	<span style="color: rgb(169, 169, 169);">Những ống ti&ecirc;m được d&ugrave;ng l&agrave;m &quot;vũ kh&iacute;&quot; trong một vụ trấn lột tr&ecirc;n đường đ&atilde; bị cảnh s&aacute;t tịch thu.</span></p>\r\n<p class="Normal">\r\n	&nbsp;</p>\r\n<p class="Normal">\r\n	<strong><font color="#4f4f4f">Một lần kh&aacute;c, tr&ecirc;n đường đi học về, Dũng, sinh vi&ecirc;n Đại học Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn (quận Thủ Đức, TP HCM)</font></strong> cũng bị bọn trấn lột d&ugrave;ng kim ti&ecirc;m khống chế để &quot;xin&quot; tiền. Cho đến nay hơn một tuần tr&ocirc;i qua, cậu học tr&ograve; qu&ecirc; Nghệ An vẫn chưa hết hoảng sợ khi thuật lại vụ việc.</p>\r\n<p class="Normal">\r\n	&quot;Mặt bọn ch&uacute;ng ngầu lắm, tr&ecirc;n người to&agrave;n h&igrave;nh xăm, tr&ocirc;ng sợ qu&aacute; n&ecirc;n em đưa 200.000 đồng mong tho&aacute;t th&acirc;n. Ai ngờ một t&ecirc;n bảo vẫn chưa đủ &#39;l&agrave;m c&ocirc;ng chuyện&#39; n&ecirc;n bắt em giao cả v&iacute; v&agrave; chiếc điện thoại. H&ocirc;m đ&oacute; em mất hơn một triệu đồng mẹ mới gửi ở qu&ecirc; v&agrave;o. &quot;Của đi thay người&quot;, th&agrave; vậy c&ograve;n đỡ hơn chống cự, lỡ nhiễm HIV thật th&igrave;...&quot;, Dũng kể.</p>\r\n<p class="Normal">\r\n	Người d&acirc;n ở khu vực quận Thủ Đức cho biết, ở đ&acirc;y chuyện bọn giang hồ d&ugrave;ng dao, kim ti&ecirc;m uy hiếp để trấn lột tiền, điện thoại, trang sức, xe m&aacute;y... của người đi đường vẫn xảy ra như cơm bữa.</p>\r\n<p class="Normal">\r\n	&quot;Cứ l&acirc;u l&acirc;u lại c&oacute; một vụ mất điện thoại, xe m&aacute;y, cả t&agrave;i xế taxi cũng bị bọn ch&uacute;ng d&ugrave;ng kim ti&ecirc;m khống chế, nhiều người kh&ocirc;ng d&aacute;m ra đường v&agrave;o ban đ&ecirc;m&quot;, b&agrave; Xu&acirc;n Thanh, chủ một nh&agrave; trọ n&oacute;i.</p>\r\n<p class="Normal">\r\n	<font color="#000000"><font color="#4f4f4f"><strong>Trao đổi với <em>VnExpress.net</em></strong><strong><font color="#4f4f4f">,</font> &ocirc;ng Nguyễn Ngọc Thanh, Ph&ograve;ng cảnh s&aacute;t điều tra tội phạm về trật tự x&atilde; hội TP HCM </strong></font><font color="#000000">cho biết, cơ quan chức năng cũng vừa bắt giữ một thanh ni&ecirc;n đ&atilde; d&ugrave;ng kim ti&ecirc;m c&oacute; HIV trấn lột 200.000 đồng v&agrave; điện thoại di động của một phụ nữ. Vụ việc xảy ra tr&ecirc;n đường Trần Quốc Thảo, phường 6, quận 3 khi nạn nh&acirc;n đang r&uacute;t tiền một m&igrave;nh ở điểm ATM.</font></font></p>\r\n<p class="Normal">\r\n	Cảnh s&aacute;t đặc nhiệm th&agrave;nh phố khuyến c&aacute;o khi xảy ra trấn lột, nạn nh&acirc;n n&ecirc;n để &yacute; c&aacute;c chi tiết nhận dạng hung thủ như biển số xe, đặc điểm cơ thể... v&agrave; đến khai b&aacute;o với cơ quan chức năng để tiến h&agrave;nh điều tra. Nếu bị chặn đường, tốt nhất n&ecirc;n nhanh tay kh&oacute;a cổ xe rồi vứt ch&igrave;a kh&oacute;a đi thật xa v&agrave; tri h&ocirc; để người d&acirc;n xung quanh đến ứng cứu...</p>\r\n<p class="Normal">\r\n	Để tr&aacute;nh bị rơi v&agrave;o tầm ngắm của bọn cướp, c&aacute;n bộ điều tra khuy&ecirc;n: &quot;Mọi người tốt nhất kh&ocirc;ng n&ecirc;n ra đường v&agrave;o thời gian từ 23h đ&ecirc;m đến 3h s&aacute;ng - thời điểm thường xảy ra cướp giật. Khi tới khu vực &iacute;t người, nhất l&agrave; r&uacute;t tiền ở c&aacute;c m&aacute;y ATM tr&ecirc;n đường, kh&ocirc;ng n&ecirc;n đi một m&igrave;nh&quot;.</p>\r\n<p class="Normal">\r\n	Trong trường hợp lỡ bị đ&acirc;m kim ti&ecirc;m v&agrave;o người, nạn nh&acirc;n kh&ocirc;ng n&ecirc;n hoảng sợ bởi theo ghi nhận của c&aacute;c cơ sở y tế, nếu được xử l&yacute; kịp thời th&igrave; tỷ lệ l&acirc;y nhiễm chỉ l&agrave; 3%. Ở nước ta, trong điều kiện b&igrave;nh thường ở ngo&agrave;i kh&ocirc;ng kh&iacute;, thời gian vi r&uacute;t HIV tồn tại trong m&aacute;u l&agrave; 3 ng&agrave;y.</p>\r\n<p class="Normal">\r\n	&quot;Nạn nh&acirc;n h&atilde;y b&igrave;nh tĩnh rửa vết thương nhiều lần bằng nước sạch, x&agrave; ph&ograve;ng, nước oxy gi&agrave;, cồn tr&ecirc;n 70 độ; nếu vết thương s&acirc;u n&ecirc;n nặn bớt m&aacute;u. Sau đ&oacute;, trong v&ograve;ng 72 giờ n&ecirc;n đến c&aacute;c trung t&acirc;m y tế gần nhất để được ch&iacute;ch phơi nhiễm ph&ograve;ng ngừa HIV&quot;, một c&aacute;n bộ dặn d&ograve;.</p>\r\n<p class="Normal" style="text-align: right;">\r\n	<strong>Ngoan Ngoan</strong></p>\r\n', 1, 1, 3, 1, 1, NULL, NULL, NULL),
(2, 'Giá vàng lao dốc xuống sát 35 triệu đồng', 'Thị trường quốc tế liên tục đi xuống và để mất hơn 40 USD trong phiên giao dịch cuối tuần, kéo giá trong nước sáng nay giảm mạnh một triệu đồng mỗi lượng. USD tự do cũng hạ nhiệt.', 'http://vnexpress.net/Files/Subject/3B/A2/2E/72/vang13bai%5B1%5D.jpg', '2010-11-13 00:00:00', '<p class="Normal" style="text-align: justify;">\r\n	L&uacute;c 8h30, mỗi lượng SJC mất 600.000 đồng chiều thu gom v&agrave; 500.000 đồng b&aacute;n ra so với cuối ng&agrave;y h&ocirc;m qua, xuống c&ograve;n 35,15-35,35 triệu đồng. Nhưng nếu so với gi&aacute; đầu ng&agrave;y 12/11, gi&aacute; mua v&agrave;o nay rẻ hơn tới 1 triệu đồng v&agrave; b&aacute;n ra thấp hơn 850.000 đồng. Khoảng nửa tiếng sau đ&oacute;, thương hiệu n&agrave;y để mất tiếp 150.000 đồng cả mua lẫn b&aacute;n xuống c&ograve;n 35-35,20 triệu đồng.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	C&ugrave;ng l&uacute;c, gi&aacute; thu gom SJC tại c&aacute;c hiệu kim ho&agrave;n ở TP HCM c&ocirc;ng bố chỉ c&ograve;n khoảng 34,90 triệu, b&aacute;n ra được k&eacute;o xuống 35,22 triệu đồng.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Gi&aacute; mua b&aacute;n v&agrave;ng SBJ của C&ocirc;ng ty Sacombank-SBJ sau khi li&ecirc;n tiếp rớt gi&aacute; v&agrave; để mất mốc 36 triệu đồng cuối ng&agrave;y h&ocirc;m qua, giờ để mất tiếp 850.000 đồng mua v&agrave;o v&agrave; 600.000 đồng b&aacute;n ra, xuống chạm 34,86-35,24 triệu đồng.</p>\r\n<p class="Normal">\r\n	<br />\r\n	<img alt="gia vang " src="http://vnexpress.net/Files/Subject/3B/A2/2E/72/vang13bai[1].jpg" style="width: 450px; height: 350px;" /></p>\r\n<p class="Normal">\r\n	<span style="color: rgb(128, 128, 128);">Gi&aacute; v&agrave;ng rơi mạnh sau sự rớt gi&aacute; kh&ocirc;ng phanh của thị trường thế giới. Ảnh: <em>Lệ Chi</em></span></p>\r\n<p class="Normal">\r\n	&nbsp;</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	C&ugrave;ng với đ&agrave; giảm mạnh tr&ecirc;n thị trường thế giới, việc c&aacute;c doanh nghiệp tung lượng v&agrave;ng mới nhập ra thị trường v&agrave; động th&aacute;i của Ng&acirc;n h&agrave;ng Nh&agrave; nước cấp th&ecirc;m quota nhập khẩu v&agrave;ng cho c&aacute;c đơn vị kinh doanh đ&atilde; g&oacute;p phần đưa khoảng c&aacute;ch gi&aacute; &#39;nội&#39; về gần hơn với gi&aacute; &#39;ngoại&#39;. Nếu lấy gi&aacute; USD 21.000 đồng, mỗi lượng v&agrave;ng thế giới l&uacute;c đ&oacute;ng cửa c&oacute; gi&aacute; quy đổi khoảng 34,7 triệu đồng (&aacute;p dụng thuế nhập khẩu 0% theo quy định mới của Bộ T&agrave;i ch&iacute;nh).</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Song song với đ&agrave; giảm mạnh của gi&aacute; v&agrave;ng, USD tại thị trường tự do s&aacute;ng nay cũng hạ nhiệt. Sau khi cố leo l&ecirc;n 21.100 đồng v&agrave;o chiều qua, gi&aacute; b&aacute;n đầu ng&agrave;y tại H&agrave; Nội giảm 20 đồng xuống c&ograve;n 21.080 đồng. Thu gom đứng y&ecirc;n 21.000 đồng. Trong khi đ&oacute;, gi&aacute; mua USD tại TP HCM thấp hơn H&agrave; Nội 50 đồng, xuống khoảng 20.950 đồng v&agrave; mua v&agrave;o dao động quanh 21.050 đồng.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	<strong><font color="#4f4f4f">Tr&ecirc;n thị trường quốc tế, gi&aacute; v&agrave;ng vừa trải qua ng&agrave;y tụt dốc mạnh nhất suốt 4 th&aacute;ng</font></strong> sau th&ocirc;ng tin Trung Quốc c&oacute; thể n&acirc;ng l&atilde;i suất cơ bản để l&agrave;m nguội bớt nền kinh tế.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Hợp đồng v&agrave;ng giao tương lai mất một mạch 37,80 USD so với đầu ng&agrave;y tương đương 2,7%, xuống c&ograve;n 1.365,50 USD trong phi&ecirc;n giao dịch điện tử tại New York. Đ&agrave; đi xuống trong ng&agrave;y cuối tuần đ&atilde; k&eacute;o tụt nỗ lực phục hồi trước đ&oacute;. Tổng cộng mỗi ounce mất 2,3% gi&aacute; trị tuần n&agrave;y.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Gi&aacute; giao ngay thậm ch&iacute; mất tới 40,1 USD, chốt tuần ở 1.368,8 USD một ounce. Đ&acirc;y l&agrave; tuần giảm đầu ti&ecirc;n của thị trường sau hai tuần li&ecirc;n tiếp gi&aacute; li&ecirc;n tục lập đỉnh mới. Mỗi ounce hiện đắt hơn 25% so với ng&agrave;y đầu năm. H&ocirc;m 9/11, thị trường giao ngay đạt đỉnh 1.424,60 USD.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	V&agrave;ng lao dốc bất chấp việc U.S Dollar Index, chỉ số đo sức mạnh của đồng đ&ocirc;la so với c&aacute;c loại ngoại tệ kh&aacute;c, hạ điểm từ 78,14 xuống 77,894. Th&ocirc;ng thường, USD mất gi&aacute; sẽ khiến v&agrave;ng đi l&ecirc;n.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Theo khảo s&aacute;t của <em>Bloomberg</em>, Trung Quốc c&oacute; thể n&acirc;ng l&atilde;i suất cho vay thời hạn một năm l&ecirc;n 5,81% v&agrave;o cuối năm nay, so với mức 5,56% hiện tại. Đ&acirc;y l&agrave; biện ph&aacute;p nhằm chống chọi lại với nguy cơ lạm ph&aacute;t tại nền kinh tế lớn thứ hai thế giới. Trong th&aacute;ng vừa rồi, lạm ph&aacute;t l&ecirc;n 4,4%, mức cao nhất trong v&ograve;ng hai năm.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Tin n&agrave;y khiến nhu cầu đầu tư v&agrave;o c&aacute;c loại h&agrave;ng h&oacute;a giảm xuống, khiến kh&ocirc;ng những v&agrave;ng m&agrave; dầu th&ocirc; cũng mất 3%. Chứng kho&aacute;n to&agrave;n cầu đi xuống sau th&ocirc;ng tin đến từ Trung Quốc.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Tuy nhi&ecirc;n, một số chuy&ecirc;n gia nhận định x&eacute;t về mặt kỹ thuật, gi&aacute; v&agrave;ng vẫn chưa ho&agrave;n to&agrave;n hết cơ hội phục hồi. &ldquo;Tuy nhi&ecirc;n nếu tuần sau biểu đồ rơi xuống 1.315 USD, c&aacute;c nh&agrave; đầu tư c&oacute; thể n&oacute;i lời tạm biệt với thời kỳ v&agrave;ng phi m&atilde;&rdquo;, một chuy&ecirc;n gia quốc tế cảnh b&aacute;o.</p>\r\n<p class="Normal" style="text-align: right;">\r\n	<strong>Lệ Chi - Thanh B&igrave;nh</strong></p>\r\n', 7, 3, 4, 1, 1, NULL, NULL, NULL),
(3, '240 người chết vì núi lửa tại Indonesia', 'Các nhân viên cứu hộ tại Indonesia tìm được vài chục người chết trong một làng gần núi lửa Merapi hôm nay, nâng tổng số người thiệt mạng vì núi lửa lên 240.', 'http://vnexpress.net/Files/Subject/3B/A2/2E/DE/m1.jpg.thumb210x0.ns.jpg', '2010-11-13 00:00:00', '<p class="Normal" style="text-align: justify;">\r\n	N&uacute;i lửa Merapi tr&ecirc;n đảo Java của Indonesia tiếp tục phun ra những đ&aacute;m tro n&oacute;ng v&agrave;o tối 12/11 v&agrave; ch&uacute;ng bay xa tới 10 km, <em>AP</em> cho biết. Trang web của Cơ quan quản l&yacute; khủng hoảng quốc gia Indonesia th&ocirc;ng b&aacute;o tổng số người chết v&igrave; n&uacute;i lửa Merapi từ ng&agrave;y 26/10 tới 13/11 tăng từ 206 l&ecirc;n 240.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Con số tử vong c&ograve;n c&oacute; thể tăng do nhiều người bị bỏng nặng c&oacute; nguy cơ tử vong. Nhiều người kh&aacute;c cũng c&oacute; thể chết do kh&oacute; thở, đau tim v&agrave; nhiều bệnh kh&aacute;c do n&uacute;i lửa g&acirc;y n&ecirc;n.</p>\r\n<p class="Normal" style="text-align: justify;">\r\n	Giới chức gặp v&ocirc; số kh&oacute; khăn trong việc thuyết phục người d&acirc;n sống gần n&uacute;i lửa ở lại c&aacute;c trại sơ t&aacute;n v&agrave; kh&ocirc;ng trở về nh&agrave;. Chiều qua một số người đ&atilde; trở về l&agrave;ng để kiểm tra ruộng vườn v&agrave; gia s&uacute;c. Họ gặp những con b&aacute;o Java chạy xuống l&agrave;ng từ một c&ocirc;ng vi&ecirc;n quốc gia gần miệng n&uacute;i lửa.</p>\r\n', 5, 2, 6, 1, 1, NULL, NULL, NULL),
(4, '6.000 dân huyện núi Khánh Sơn bị cô lập sau lũ', 'Nhiều đoạn đường sạt lở, cầu tại tỉnh lộ 9 bị cuốn trôi sau hai tuần mưa lũ đã khiến 6.000 người dân thuộc 2 xã Sơn Lâm, Thành Sơn huyện núi Khánh Sơn, Khánh Hòa rơi vào tình trạng cô lập.', 'http://vnexpress.net/Files/Subject/3B/A2/2E/E4/phu-nu-Raglai-qua-song-2.jpg', '2010-11-13 00:00:00', '<p style="text-align: justify;">\r\n	Quan s&aacute;t của <em>VnExpress.net</em>, tỉnh lộ 9 chạy s&aacute;t v&aacute;ch n&uacute;i, tuyến đường vốn trắc trở nay c&agrave;ng nguy hiểm hơn v&igrave; t&igrave;nh trạng sạt lở k&eacute;o d&agrave;i. Ri&ecirc;ng ở km 26+100, đoạn đường n&agrave;y bị sạt lở nghi&ecirc;m trọng với chiều d&agrave;i khoảng 20m, độ rộng 7m, khối lượng đất đ&aacute; sạt ước t&iacute;nh 10.000 m3. To&agrave;n bộ nền đường đều bị sạt tr&ocirc;i, kho&eacute;t s&acirc;u v&agrave;o n&uacute;i.</p>\r\n<p style="text-align: justify;">\r\n	<img alt="1" src="http://vnexpress.net/Files/Subject/3B/A2/2E/E4/sat-lo-nghiem-trong.jpg" style="width: 450px; height: 350px;" /></p>\r\n<p style="text-align: justify;">\r\n	<img alt="" src="http://vnexpress.net/Files/Subject/3B/A2/2E/E4/day-xe-qua-doan-sat-lo.jpg" style="width: 450px; height: 350px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<img alt="" src="http://vnexpress.net/Files/Subject/3B/A2/2E/E4/Khieng-xe-qua-song-gia-20-n.jpg" style="width: 450px; height: 350px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<img alt="" src="http://vnexpress.net/Files/Subject/3B/A2/2E/E4/di-chuyen-hang-qua-song-ban.jpg" /></p>\r\n<p style="text-align: justify;">\r\n	<img alt="" src="http://vnexpress.net/Files/Subject/3B/A2/2E/E4/cong-gao-tiep-te-cho-dan.jpg" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<img alt="" src="http://vnexpress.net/Files/Subject/3B/A2/2E/E4/sap-cau-nhieu-noi.jpg" style="width: 450px; height: 350px;" /></p>\r\n<p style="text-align: justify;">\r\n	Nhiều c&acirc;u cầu bị sập c&ograve;n trơ những tảng b&ecirc; t&ocirc;ng</p>\r\n<p style="text-align: right;">\r\n	<strong>Tường Vi</strong></p>\r\n', 16, 1, 2, 1, 1, NULL, NULL, NULL),
(5, 'Đột kích bar trung tâm Sài Gòn', 'Rạng sáng nay, gần 30 cảnh sát bất ngờ đột kích vào quán bar BB Club 96 Nguyễn Thị Minh Khai (quận 3, TP HCM). Khoảng 200 người có mặt bên trong chạy tán loạn', 'http://vnexpress.net/Files/Subject/3B/A2/2F/4E/quan_bar.jpg', '2010-11-15 00:00:00', '<p>\r\n	&nbsp;</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\n	Khoảng 2h s&aacute;ng, Đội cảnh s&aacute;t quản l&yacute; h&agrave;nh ch&iacute;nh kết hợp c&ugrave;ng Đội cảnh s&aacute;t điều tra tội phạm về trật tự x&atilde; hội c&ocirc;ng an quận 3 đ&atilde; bất ngờ đột k&iacute;ch bar BB Club 96 Nguyễn Thị Minh Khai.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\n	Khi cảnh s&aacute;t xuất hiện, b&ecirc;n trong qu&aacute;n c&oacute; khoảng 200 kh&aacute;ch. Tiếng nhạc vẫn được mở với c&ocirc;ng suất lớn. Thấy b&oacute;ng cảnh s&aacute;t, nhiều người x&ocirc; cửa chạy ra ngo&agrave;i, g&acirc;y n&ecirc;n cảnh hỗn loạn.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	<img alt="quan bar BB" src="http://vnexpress.net/Files/Subject/3B/A2/2F/4E/quan_bar.jpg" style="width: 480px; height: 327px; " /></p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	<span style="color:#a9a9a9;">Khoảng 200 người vẫn vui chơi trong bar d&ugrave; đ&atilde; 2h s&aacute;ng. Ảnh:&nbsp;<em>An Nhơn.</em></span></p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	&nbsp;</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\n	Gần 30 cảnh s&aacute;t đ&atilde; chốt chặn tại cửa y&ecirc;u cầu kiểm tra h&agrave;nh ch&iacute;nh. Tuy nhi&ecirc;n, một số kh&aacute;ch trong trạng th&aacute;i say vẫn tiếp tục nhảy nh&oacute;t v&agrave; h&ograve; h&eacute;t bất chấp cảnh c&aacute;o của lực lượng chức năng.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\n	Đến gần 4h s&aacute;ng nay, những người xuất tr&igrave;nh đầy đủ giấy tờ đ&atilde; được cho về. Cuộc kiểm tra k&eacute;o d&agrave;i nhiều giờ gồm c&aacute;c lực lượng h&igrave;nh sự, đặc nhiệm đ&atilde; ph&aacute;t hiện 1,5 vi&ecirc;n n&eacute;n m&agrave;u hồng h&igrave;nh tr&aacute;i tim nghi l&agrave; ma t&uacute;y tổng hợp được giấu dưới nệm ghế ph&iacute;a g&oacute;c ph&ograve;ng.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	<img alt="" src="http://vnexpress.net/Files/Subject/3B/A2/2F/4E/bat_2.jpg" style="width: 480px; height: 320px; " /></p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	<span style="color:#a9a9a9;">Vi&ecirc;n thuốc m&agrave;u hồng được t&igrave;m thấy dưới ghế nệm. Ảnh:&nbsp;<em>An Nhơn.</em></span></p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	&nbsp;</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\n	Đơn vị kinh doanh qu&aacute;n bar đ&atilde; bị lập bi&ecirc;n bản vi phạm h&agrave;nh ch&iacute;nh về h&agrave;nh vi hoạt động qu&aacute; giờ quy định. 1,5 vi&ecirc;n n&eacute;n thu được trong qu&aacute; tr&igrave;nh kh&aacute;m x&eacute;t được chuyển Ph&ograve;ng khoa học kỹ thuật h&igrave;nh sự gi&aacute;m định.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\n	Đ&acirc;y l&agrave; lần thứ 2 qu&aacute;n bar BB Club 96 Nguyễn Thị Minh Khai bị cảnh s&aacute;t kiểm tra v&agrave; từng ph&aacute;t hiện c&oacute; thuốc lắc. Trước đ&oacute;, th&aacute;ng 8/2009, qu&aacute;n bar n&agrave;y đ&atilde; bị Đo&agrave;n kiểm tra li&ecirc;n ng&agrave;nh văn h&oacute;a x&atilde; hội kết hợp với Ph&ograve;ng cảnh s&aacute;t quản l&yacute; h&agrave;nh ch&iacute;nh c&ocirc;ng an TP HCM lập bi&ecirc;n bản xử phạt v&igrave; hoạt động qu&aacute; giờ quy định, thiếu giấy ph&eacute;p hoạt động v&agrave; c&oacute; ma t&uacute;y tổng hợp trong cơ sở kinh doanh.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: right; ">\r\n	<strong>An Nhơn</strong></p>\r\n<p align="right" class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	<strong designtimesp="31311">An Nhơn</strong></p>\r\n<p align="right" class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	&nbsp;</p>\r\n<p align="right" class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	&nbsp;</p>\r\n<p align="right" class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	&nbsp;</p>\r\n<p align="right" class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	&nbsp;</p>\r\n<p align="right" class="Normal" style="font-family: ''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 18, 1, 3, 1, 1, 'Saigon center bar raid', '<p>\r\n</ P>\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\nKhoang 2h present, Change CẢNH SÁT administrative lý actions primary combined with the same Change CẢNH SÁT THE tra toi range về Trat Remote exchange Công An Quận 3 enabled expected conficts size bar BB Club 96 Nguyễn Thị Minh Khai </ p>\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\nWhen CẢNH SÁT appear within Quân have a period 200 Client. Tiếng nhạc still opened with public exchange greater. Resolve bóng CẢNH SÁT, many people Xô window running ra ngoai, give and Scene Hòn vay </ p>\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n<img alt="quan thanh BB" src="http://vnexpress.net/Files/Subject/3B/A2/2F/4E/quan_bar.jpg" style="width: 480px; height: 327px; "/> </ p>\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n<span style="color:#a9a9a9;"> Khoang 200 people still vui play in bar even have 2h highlight. Ảnh:. <em> An Nhơn </ em> </ span> </ p>\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n</ P>\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\nAlmost 30 CẢNH SÁT have chợt chan at window requests check for action primary. However, some of clients in state thái nói still continues to jump nhót and Hồ hét any take Scene cáo Cửa Lục quality functionality </ p>\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); text-align: justify; ">\r\nĐến almost 4h Sang Nay, the people export process đầy đủ paper tờ has been cho về. Conversation check for extended multiple giờ enabled to include the quality screen sự, đặc Nhiệm has been detected 1,5 Computer Compression colors hồng screen left tim nghi that ma tuy total combined are Giàu under the nem ghe Phia corner room </ p>.\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n<img alt="" src="http://vnexpress.net/Files/Subject/3B/A2/2F/4E/bat_2.jpg" style="width: 480px; height: 320px; "</ p >\r\n<p class="Normal" style="color:''Times New Roman''; font-size: 11.8pt; color: rgb(0, 0, 0); ">\r\n<span style="color:#a9a9a9;"> Viên Thước colors hồng be found under the ghe nem. Ảnh:. <em> An Nhơn </ em> </ span> </ p>\r\n<p cla